clear all; close all; clc;Fs = 1000;Ts = 1/Fs;t = 0:Ts:0.1;x = sin(2*pi*120*t) + sin(2*pi*60*t);figure(1)  plot(t,x);  xlabel('t[s]'); ylabel('x(t)'); title('Signal x(t)');  grid on;%Neadekvatna frekvencija odabiranja Fs = 200HzFs1 = 200;Ts1 = 1/Fs1;t1 = 0:Ts1:0.1;x1 = sin(2*pi*120*t1) + sin(2*pi*60*t1);figure(2)  plot(t, x);  hold on;  scatter(t1,x1,'g', '*');  grid on;  xlabel('t[s]'); ylabel('x(t)'); title('Signal x(t)');  %Signal sa zamenjenim ucestanostima  x3 = sin(2*pi*80*t + pi) + sin(2*pi*60*t);  plot(t, x3, 'r');  legend('originalni x(t)', 'odabrani x(t) - Fs=200Hz', 'x(t) zamenjene ucestanosti');